contracts/Tunnel.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;
import "@openzeppelin/contracts/access/Ownable.sol";

import "hardhat/console.sol";

contract Tunnel is Ownable {
	enum TokenStatus {
		None,
		Init,
		Mapped
	}

	// {from chain}-{type of message}
	enum MessageType {
		L1MappingInit,
		L2MappingComplete,
		L1TransferToL2,
		L2TransferToL1
	}

	struct ERC721MappedData {
		address L1Address;
		address L2Address;
		string name;
		string symbol;
		address tokenContractOwner;
	}

	struct ERC721Transfer {
		address L1Address;
		address from;
		uint256 tokenId;
		string tokenURI;
	}
}

